import { useState } from "react";
import "./App.css";
import * as Parser from "./parser/formula-parser.js";
const parse = Parser.parse;

function getOperator(type) {
  if (type === "ADDITION") return "+";
  else if (type === "DIVISION") return "/";
  else if (type === "MULTIPLICATION") return "*";
  else if (type === "SUBTRACTION") return "-";
  else return;
}

function getValue(node) {
  if (node.type !== "FUNCTION" || node.type !== "PAREN")
    return node.name || node.value;
}

function getSpan(name, size) {
  return (
    <span style={{ paddingBottom: size, border: "0.5px solid" }} key={size}>
      {name}
    </span>
  );
}

function App() {
  let [formula, formulaChange] = useState(
    "($a+SQRT(SQR($b)-4*$c))/(2*$d)"
    //"1 + 2 + 3 + 4 + 5 + 6"
    //"($c+SQRT(SQR($b)-4*$a))"
  );
  let [syntaxTree, syntaxTreeChange] = useState("");
  let [syntaxTreeJson, syntaxTreeJsonChange] = useState(null);
  let [visualizerOutput, visualizerChange] = useState("");
  let [listItem, updateList] = useState("");
  const styles = {
    treeLayout: {
      border: "0.5px solid",
    },
    container: {
      height: 30,
      width: 100,
    },
  };
  const updateAst = () => {
    //console.log("creating ast view...");
    const newSyntaxTree = parse(formula);
    syntaxTreeChange(newSyntaxTree);

    //console.log("The ast is: ", syntaxTree);
    syntaxTreeJsonChange(JSON.stringify(newSyntaxTree, null, 2));
    // console.log(expr);
  };

  const trees = [];
  const dfs = (node) => {
    if (!node) return;
    /// special case for expression and arguements
    // get subtrees
    dfs(node.left);
    if (node.left && node.right) {
      // add for vairable name
      if (node.left.value && node.right.value) {
        trees.push(node.left.value + node.type + node.right.value);
      } else if (node.right.value) {
        trees.push(node.type + node.right.value);
      } else if (node.left.value) {
        trees.push(node.left.value + node.type);
      }
    }
    dfs(node.right);
  };

  const convertAstToFormula = () => {
    console.log("converting ast to string...");
    // create string expression from tree
    let expr = "";
    let formulaList = [];

    const createFormulaString = (node) => {
      // use Inorder traversal for tree
      if (!node) return;
      createFormulaString(node.left);
      if (node.value ?? getOperator(node.type) ?? node.name) {
        expr += node.value ?? getOperator(node.type) ?? node.name;
      }
      // special casing for parenthesis
      if (node.expression) {
        expr += "(";
        createFormulaString(node.expression);
        expr += ")";
      }
      // special casing for parenthesis
      if (node.arguments) {
        expr += "(";
        createFormulaString(node.arguments[0]);
        expr += ")";
      }
      createFormulaString(node.right);
    };

    createFormulaString(JSON.parse(syntaxTreeJson));
    dfs(JSON.parse(syntaxTreeJson));

    trees.forEach((item) => {
      console.log(item);
    });

    visualizerChange(expr);
    updateList(formulaList);
  };

  return (
    <div className="formulizer">
      <h1>Welcome to the formulizer!</h1>
      <h3>Input formula</h3>
      <p>
        <textarea
          cols={100}
          rows={8}
          value={formula}
          onChange={(event) => formulaChange(event.target.value)}
        />{" "}
        <br />
      </p>
      <p>
        <button onClick={updateAst}>Parse and update AST View</button>
      </p>
      <h3>Syntax tree</h3>
      <pre
        style={{
          maxHheight: "300px",
          overflowy: "auto",
          backgroundColor: "#eeeeee",
        }}
      >
        {syntaxTreeJson}
      </pre>
      <p>
        <button onClick={convertAstToFormula}>Convert AST to Formula</button>
      </p>
      <h3>Visualizer-to-Formula</h3>
      <p>{visualizerOutput}</p>
      <div style={styles.container}>{listItem}</div>
      {}
    </div>
  );
}

export default App;

